mapper.select:
SELECT Fuzzy_query.* FROM ( SELECT information.*, tag.tag_name FROM ( SELECT Commodity_info.*, count(t_com.id) AS comment_number FROM ( SELECT a.*, city.country, city.province, city.city, city.description AS city_description FROM ( SELECT id, city_id, hotel_name AS `name`, picture, description, price, mark, p_type, travel_agency_id, tags_id, views FROM t_hotel UNION SELECT id, city_id, scence_name AS `name`, picture, description, price, mark, p_type, travel_agency_id, tags_id, views FROM t_scence UNION SELECT id, city_id, restaurant_name AS `name`, picture, description, price, mark, p_type, travel_agency_id, tags_id, views FROM t_restaurant ) AS a LEFT JOIN t_city city ON a.city_id = city.id ) AS Commodity_info LEFT JOIN t_comment AS t_com ON t_com.big_type = Commodity_info.p_type AND t_com.commodity_id = Commodity_info.id GROUP BY Commodity_info.id, Commodity_info.p_type ORDER BY Commodity_info.p_type ) AS information LEFT JOIN t_tags tag ON tag.id = information.tags_id ) AS Fuzzy_query
CONCAT( Fuzzy_query.country, Fuzzy_query.city, Fuzzy_query.province, Fuzzy_query.name ) LIKE "%"#{keyword}"%" and CONCAT(Fuzzy_query.country,Fuzzy_query.city,Fuzzy_query.province) LIKE "%"#{region}"%" and Fuzzy_query.p_type = #{classification}
CONCAT( Fuzzy_query.country, Fuzzy_query.city, Fuzzy_query.province, Fuzzy_query.name ) LIKE "%" #{keyword}"%" and CONCAT(Fuzzy_query.country,Fuzzy_query.city,Fuzzy_query.province) LIKE "%"#{region}"%"
CONCAT( Fuzzy_query.country, Fuzzy_query.city, Fuzzy_query.province, Fuzzy_query.name ) LIKE "%"#{keyword}"%" and Fuzzy_query.p_type = #{classification}
CONCAT(Fuzzy_query.country,Fuzzy_query.city,Fuzzy_query.province) LIKE "%" #{region}"%" and Fuzzy_query.p_type = #{classification}
Fuzzy_query.name LIKE "%"#{keyword}"%"
CONCAT(Fuzzy_query.country,Fuzzy_query.city,Fuzzy_query.province) LIKE "%"#{region}"%"
and Fuzzy_query.p_type = #{classification}
and Fuzzy_query.price >= #{max} and Fuzzy_query.price <= #{min}
and Fuzzy_query.mark >= #{mark}
and Fuzzy_query.mark <= #{mark}
